void CMouse::SetLeftButton ( int iState )
{
	if ( iState == 1 )
	{
		if ( m_tLeftButton.Running () )
		{
			m_iLeftButton = 2;
			m_tLeftButton.Stop ();
		}
		else
		{
			m_iLeftButton = 1;
			m_tLeftButton.Start ( DBLCLICK_TIME );
		}
	}
	else
		m_iLeftButton = iState;
}

